const e=JSON.parse('{"key":"v-5fe2c817","path":"/Web%E5%AE%89%E5%85%A8/XSS%E8%B7%A8%E7%AB%99%E8%84%9A%E6%9C%AC%E6%94%BB%E5%87%BB.html","title":"XSS跨站脚本攻击","lang":"zh-CN","frontmatter":{"title":"XSS跨站脚本攻击","summary":"漏洞概述 漏洞成因 当应用程序发送给浏览器的页面中包含用户提交的数据，但没有经过适当验证或转义时，就会导致跨站脚本漏洞。会造成Cookie窃取等危害。 XSS漏洞的防御方法 1）添加HTTP-only标头：禁止javascript读取敏感Cookie，即使攻击者能xss也不能获取Cookie 2）使用CSP浏览器内容安全策略白名单机制：对网站加载和执行的资源","head":[["meta",{"property":"og:url","content":"https://wintrysec.github.io/Web%E5%AE%89%E5%85%A8/XSS%E8%B7%A8%E7%AB%99%E8%84%9A%E6%9C%AC%E6%94%BB%E5%87%BB.html"}],["meta",{"property":"og:site_name","content":"网络安全知识库"}],["meta",{"property":"og:title","content":"XSS跨站脚本攻击"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}]]},"excerpt":"","headers":[{"level":2,"title":"漏洞概述","slug":"漏洞概述","link":"#漏洞概述","children":[]},{"level":2,"title":"漏洞分类","slug":"漏洞分类","link":"#漏洞分类","children":[]},{"level":2,"title":"查找验证XSS漏洞","slug":"查找验证xss漏洞","link":"#查找验证xss漏洞","children":[]},{"level":2,"title":"CSP内容安全策略","slug":"csp内容安全策略","link":"#csp内容安全策略","children":[{"level":3,"title":"两种方法启用CSP","slug":"两种方法启用csp","link":"#两种方法启用csp","children":[]},{"level":3,"title":"绕过CSP","slug":"绕过csp","link":"#绕过csp","children":[]}]},{"level":2,"title":"同源策略","slug":"同源策略","link":"#同源策略","children":[{"level":3,"title":"如何规避同源策略？","slug":"如何规避同源策略","link":"#如何规避同源策略","children":[]},{"level":3,"title":"JSONP","slug":"jsonp","link":"#jsonp","children":[]},{"level":3,"title":"CORS","slug":"cors","link":"#cors","children":[]}]}],"git":{},"readingTime":{"minutes":7.29,"words":2186},"copyright":"著作权归所有\\n原文链接：https://wintrysec.github.io/Web%E5%AE%89%E5%85%A8/XSS%E8%B7%A8%E7%AB%99%E8%84%9A%E6%9C%AC%E6%94%BB%E5%87%BB.html","filePathRelative":"Web安全/XSS跨站脚本攻击.md"}');export{e as data};
